import React, {Component} from 'react';
import './Authen.css'
import {Link} from "react-router-dom";
import {NotificationManager} from "react-notifications";
import {callApi} from "../../utils/apiCaller";
import {ToastContainer, ToastStore} from "react-toasts";

class Register extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: '',
            rePassword: ''
        }
    }

    handleChange = (event) => {
        const target = event.target;
        let value = target.value;
        if(target.type === 'file') {
            value = target.files;
        } else if(target.type === 'checkbox') {
            value = target.checked;
        }
        const name = target.name;
        this.setState({
            [name]: value
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
        let {email, password, rePassword} = this.state;
        if(password !== rePassword) {
            console.log('password and re-type password must same');
            ToastStore.error("There is an error :'(")
            return;
        }
        let params = {
            email: email,
            password: password
        };
        this.setState({
            isLoading: true
        });
        callApi("authen/signup", 'POST', params).then(res => {
            console.log(res);
            NotificationManager.success('Success message', '', 1000, null, true);
            this.setState({
                isLoading: false,
                isDisplayAlert: true,
                messageAlert: 'Tạo sảm phẩm thành công'
            });
        });
    };

    render() {
        let {email, password, rePassword} = this.state;
        return (
            <div className="container col-md-4 form">
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label For="exampleInputEmail1">Email address</label>
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Enter email"
                            name="email"
                            value={email}
                            onChange={this.handleChange}
                        />
                        <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone
                            else.
                        </small>
                    </div>
                    <div className="form-group">
                        <label For="exampleInputPassword1">Password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Password"
                            name="password"
                            value={password}
                            onChange={this.handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label For="exampleInputPassword1">Re-type password</label>
                        <input
                            type="password"
                            className="form-control"
                            placeholder="Re-type password"
                            name="rePassword"
                            value={rePassword}
                            onChange={this.handleChange}
                        />
                    </div>
                    <button type="submit" className="btn btn-warning">Register</button>
                </form>
                <ToastContainer position={ToastContainer.POSITION.TOP_RIGHT} store={ToastStore}/>
            </div>
        );
    }
}

export default Register;
