import React, {Component} from 'react';
import './Dialog.css'

class Dialog extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isDisplay: false,
            message: '',
            isDelete: false
        }
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
            isDisplay: nextProps.isDisplay,
            message: nextProps.message,
            isDelete: nextProps.isDelete
        });
    }

    render() {
        let {isDisplay, message} = this.state;
        return (
            <div className={isDisplay ? 'bg-shadow center show' : 'hidden'}>
                <div className="bg-dialog">
                    <h4 className="title-dialog">Thông báo</h4>
                    <p className="message-dialog">{message}</p>
                    <div className="btn-parent center">
                        <button className="btn btn-dialog-left">Không</button>
                        <button className="btn btn-dialog-right">Có</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Dialog;
