import * as Config from "../constants/Config";
import axios from 'axios'

function callApi(endpoint, method = 'GET', body = null, headers = null) {
    return axios({
        method: method,
        url: `${Config.API_URL}/${endpoint}`,
        data: body,
        headers: headers
    }).catch(err => {
       console.log(err)
    });
}

function getApi(endpoint, params = null, headers = null) {
    return axios.get(`${Config.API_URL}/${endpoint}`, params).catch(err => {
        console.log(err)
    });
}
