import React, {Component} from 'react';
import './Alert.css'

class Alert extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isDisplay: false
        }
    }

    componentDidMount() {
    }

    render() {
        console.log(this.state);
        let {isDisplay, message} = this.props;
        this.setState({
           isDisplay: true
        });
        if(isDisplay) {
            setTimeout(function() {
                this.setState({isDisplay: false})
            }.bind(this), 1000)
        }
        return (
            <div className={isDisplay ? 'ng-alert-success show' : 'hidden'}>
                Vì em, vì em
            </div>
        );
    }
}

export default Alert;
