import React, {Component} from 'react';
import callAPi from './../../utils/apiCaller'

class HomePage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            projectName: '',
            investor: '',
            address: '',
            area: '',
            time: '',
            file: null
        }
    };

    handleChange = (event) => {
        const target = event.target;
        const  value = target.type === 'file' ? target.files[0] : target.value;
        const name = target.name;
        this.setState({
            [name]: value
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state);
        let {projectName, investor, address, area, time} = this.state;
        callAPi('product/create', 'POST', {
            projectName: projectName,
            investor: investor,
            address: address,
            area: area,
            time: time
        }).then(res => {
            console.log(res);
            this.uploadImage()
        });
    };

    uploadImage() {
        const formData = new FormData();
        formData.append('name1', 'this is a');
        formData.append('file', this.state.file, this.state.file.fileName);
        const headers = {
            'content-type': 'multipart/form-data'
        };
        callAPi("file/upload", 'POST', formData, null).then(res => {
            console.log(res)
        });
    }

    render() {
        return (
            <div className="container mt-5">
                <form onSubmit={this.handleSubmit} encType="multipart/form-data">
                    <div className="form-group">
                        <label>Dự án</label>
                        <input type="text" className="form-control" name="projectName" value={this.state.projectName} onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <label>Chủ đầu tư</label>
                        <input type="text" className="form-control" name="investor" value={this.state.investor} onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <label>Địa chỉ</label>
                        <input type="text" className="form-control" name="address" value={this.state.address} onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <label>Diện tích</label>
                        <input type="text" className="form-control" name="area" value={this.state.area} onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <label>Thời gian</label>
                        <input type="text" className="form-control" name="time" value={this.state.time} onChange={this.handleChange}/>
                    </div>
                    <div className="form-group">
                        <input type="file" name="file" onChange={this.handleChange}/>
                    </div>
                    <button type="submit" className="btn btn-primary mt-2">Tạo dự án</button>
                </form>
            </div>
        );
    }
}

export default HomePage;
