import React, {Component} from 'react';
import './MainProduct.css'
import ProductItem from "../../components/ProjectItem/ProductItem";
import {getApi} from "../../utils/apiCaller";
import {Link} from "react-router-dom";
import Pagination from "react-js-pagination";
import Dialog from "../../components/Alert/Dialog";

class MainProject extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: [],
            page: 1,
            pageSize: 5,
            total: 0
        }
    }

    componentDidMount() {
        this.getListProduct(1);
    }

    getListProduct(pageNumber) {
        let params = {
            page: pageNumber,
            pageSize: this.state.pageSize
        };
        getApi('product', params).then(res => {
            if(res.data.success) {
                this.setState({
                    products: res.data.data,
                    total: res.data.total
                });
            }
        })
    }

    handlePageChange = (pageNumber) => {
        this.setState({page: pageNumber});
        this.getListProduct(pageNumber);
    };

    render() {
        let {products} = this.state;
        return (
            <div>
                <Dialog/>
                <div className="main">
                    <div>
                        <Link className="btn btn-secondary" to="/create-product">
                            <span className="fa fa-plus"/>Thêm sản phẩm
                        </Link>
                    </div>
                    <div className="table-responsive">
                        <table className="table table-condensed">
                            <thead>
                            <tr>
                                <th>Hình ảnh</th>
                                <th>Tên dự án</th>
                                <th>Thể loại</th>
                                <th>Chủ đầu tư</th>
                                <th>Địa chỉ</th>
                                <th>Diện tích</th>
                                <th>Thời gian</th>
                                <th></th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.showProducts(products)}
                            </tbody>
                        </table>
                    </div>
                    <div className="d-flex justify-content-center">
                        <Pagination
                            hideDisabled
                            activePage={this.state.page}
                            itemsCountPerPage={this.state.pageSize}
                            totalItemsCount={this.state.total}
                            pageRangeDisplayed={3}
                            onChange={this.handlePageChange}
                            linkClass='page-link'
                        />
                    </div>
                </div>
            </div>
        );
    }

    showProducts(products) {
        let result = null;
        if(products.length > 0) {
            result = products.map((product, index) => {
                return (
                    <ProductItem
                        product={product}
                    />
                );
            });
        }
        return result;
    }
}

export default MainProject;
