import React, {Component} from 'react';
import './MainProduct.css'
import ProductItem from "../../components/ProjectItem/ProductItem";
import callApi from "../../utils/apiCaller";

class MainProject extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: []
        }
    }

    componentDidMount() {
        callApi('product', 'GET', null).then(res => {
            console.log(res);
            this.setState({
                products: res.data.data
            });
        })
    }

    render() {
        let {products} = this.state;
        return (
            <div className="main">
                <div>
                    <button className="btn btn-danger" onClick={this.addProduct}>
                        <span className="fa fa-plus"/>Thêm sản phẩm
                    </button>
                </div>
                <div className="table-responsive">
                    <table className="table table-condensed">
                        <thead>
                        <tr>
                            <th>Hình ảnh</th>
                            <th>Tên dự án</th>
                            <th>Thể loại</th>
                            <th>Chủ đầu tư</th>
                            <th>Địa chỉ</th>
                            <th>Diện tích</th>
                            <th>Thời gian</th>
                        </tr>
                        </thead>
                        <tbody>
                        {this.showProducts(products)}
                        </tbody>
                    </table>
                </div>
            </div>
        );
    }

    addProduct() {

    }

    showProducts(products) {
        let result = null;
        if(products.length > 0) {
            result = products.map((product, index) => {
                return (
                    <ProductItem
                        product={product}
                    />
                );
            });
        }
        return result;
    }
}

export default MainProject;
