import React, {Component} from 'react';
import './MainProduct.css'
import ProductItem from "../../components/ProjectItem/ProductItem";
import {deleteApi, getApi} from "../../utils/apiCaller";
import {Link} from "react-router-dom";
import Pagination from "react-js-pagination";
import Dialog from "../../components/Alert/Dialog";

class MainProject extends Component {

    constructor(props) {
        super(props);
        this.state = {
            products: [],
            page: 1,
            pageSize: 5,
            total: 0,
            isDelete: false,
            deleteProduct: null
        }
    }

    componentDidMount() {
        this.getListProduct(1);
    }

    getListProduct(pageNumber) {
        let params = {
            page: pageNumber,
            pageSize: this.state.pageSize
        };
        getApi('product', params).then(res => {
            if(res.data.success) {
                this.setState({
                    products: res.data.data,
                    total: res.data.total
                });
            }
        })
    }

    handlePageChange = (pageNumber) => {
        this.setState({page: pageNumber});
        this.getListProduct(pageNumber);
    };

    showProducts(products) {
        let result = null;
        if(products.length > 0) {
            result = products.map((product, index) => {
                return (
                    <ProductItem delete = {this.delete} product={product}/>
                );
            });
        }
        return result;
    }

    delete = (product) => {
        this.setState({
            isDelete: true,
            deleteProduct: product
        });
    };

    okDelete = (product) => {
        let params = {
            id : product._id
        };
        deleteApi('product/delete', params).then(res => {
            if(res.data.success) {
                let {products} = this.state;
                for(let i = 0; i < products.length; i++) {
                    if(products[i]._id === product._id) {
                        products.splice(i, 1);
                    }
                }
                this.setState({
                    isDelete: false,
                    products: products
                })
            }
        })
    };

    sort = () => {
        console.log('X======> sort');
        let {products} = this.state;
        products.sort((a, b) => {

        });
    };

    render() {
        let {products, isDelete, deleteProduct} = this.state;
        return (
            <div>
                <Dialog
                    product = {deleteProduct}
                    isDelete = {isDelete}
                    message = 'Bạn có muốn xoá sản phẩm này?'
                    okDelete = {this.okDelete}
                />
                <div className="main">
                    <div>
                        <Link className="btn btn-secondary" to="/create-product">
                            <span className="fa fa-plus"/>Thêm sản phẩm
                        </Link>
                    </div>
                    <div className="table-responsive">
                        <table className="table table-condensed">
                            <thead>
                            <tr>
                                <th>Hình ảnh</th>
                                <th>Tên dự án</th>
                                <th>Thể loại</th>
                                <th>Chủ đầu tư</th>
                                <th>Địa chỉ</th>
                                <th>Diện tích<span className="fa fa-sort ml-1" onClick={this.sort}/></th>
                                <th>Thời gian<span className="fa fa-sort ml-1" onClick={this.sort}/></th>
                                <th/>
                            </tr>
                            </thead>
                            <tbody>
                            {this.showProducts(products)}
                            </tbody>
                        </table>
                    </div>
                    <div className="d-flex justify-content-center">
                        <Pagination
                            hideDisabled
                            activePage={this.state.page}
                            itemsCountPerPage={this.state.pageSize}
                            totalItemsCount={this.state.total}
                            pageRangeDisplayed={3}
                            onChange={this.handlePageChange}
                            linkClass='page-link'
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default MainProject;
