import React, {Component} from 'react';
import './Alert.css'

class Alert extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isDisplay: this.props.isDisplay
        }
    }

    componentDidMount() {
    }

    componentWillReceiveProps(nextProps) {
        console.log('componentWillReceiveProps = ' + nextProps.isDisplay);
        this.setState({
            isDisplay: nextProps.isDisplay
        });
        console.log("componentWillReceiveProps isDisplay = " + this.state.isDisplay);
        if(nextProps.isDisplay) {
            console.log("componentWillReceiveProps = XXX");
            setTimeout(() => {this.setState({isDisplay: false}); }, 1);
        }
    }

    render() {
        let {isDisplay, message} = this.state;
        return (
            <div className={isDisplay ? 'ng-alert-success show' : 'hidden'}>
                Vì em, vì em
            </div>
        );
    }
}

export default Alert;
