import React, {Component} from 'react';

const menu = [
    {
        name: 'Trang Chủ',
        to: '/',
        exact: true
    },
    {
        name: 'Giới Thiệu',
        to: '/introduction',
        exact: true
    },
    {
        name: 'Xưởng Sản Xuất',
        to: '/factory',
        exact: true
    },
    {
        name: 'Thiết Kế Nội Thất',
        to: '/design',
        exact: true
    },
    {
        name: 'Thi Công Nội Thất',
        to: '/construction',
        exact: true
    },
    {
        name: 'Thiết Kế Mẫu',
        to: '/sample-design',
        exact: true
    },
    {
        name: 'Liên Hệ',
        to: '/contact',
        exact: true
    }
];

const MenuLink = ({name, to, exact}) => {
    return (
        <Route
            path={to}
            exact={exact}
            children={({match}) => {
                let active = match ? 'active' : '';
                return (
                    <li className={`nav-item nav-link ${active}`}>
                        <Link to={to}>
                            {name}
                        </Link>
                    </li>
                )
            }}
        />
    );
};

class Menu extends Component {
    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-light">
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav">
                            <li className="nav-item active">
                                <a className="nav-link" href="#">Trang Chủ <span
                                    className="sr-only">(current)</span></a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Giới Thiệu</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Xưởng Sản xuất</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Thiết Kế Nột Thất</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Thi Công Nột Thất</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Thiết Kế Mẫu</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link" href="#">Liên Hệ</a>
                            </li>
                        </ul>
                    </div>
                </nav>
            </div>
        );
    }


    showMenu = (menus) => {
        var result = null;
        if(menus.length > 0) {
            result = menus.map((menu, index) => {
                return (
                    <MenuLink
                        key={index}
                        label={menu.name}
                        to={menu.to}
                        exact={menu.exact}
                    />
                );
            })
        }
        return result;
    }

