import React, {Component} from 'react';
import {Link, Route} from "react-router-dom";
import './Menu.css';

const menus = [
    {
        name: 'Trang Chủ',
        to: '/',
        exact: true
    },
    {
        name: 'Giới Thiệu',
        to: '/introduction',
        exact: true
    },
    {
        name: 'Xưởng Sản Xuất',
        to: '/factory',
        exact: true
    },
    {
        name: 'Thiết Kế Nội Thất',
        to: '/design',
        exact: true
    },
    {
        name: 'Thi Công Nội Thất',
        to: '/construction',
        exact: true
    },
    {
        name: 'Thiết Kế Mẫu',
        to: '/sample-design',
        exact: true
    },
    {
        name: 'Liên Hệ',
        to: '/contact',
        exact: true
    }
];

const MenuLink = ({name, to, exact}) => {
    return (
        <Route
            path={to}
            exact={exact}
            children={({match}) => {
                let active = match ? 'active' : '';
                return (
                    <li className={`nav-item nav-link ${active}`}>
                        <Link to={to} className="nav-link">
                            {name}
                        </Link>
                    </li>
                )
            }}
        />
    );
};

class Menu extends Component {
    render() {
        return (
            <div className="sticky">
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <a className="navbar-brand" href="#">Home Group</a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse"
                            data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false"
                            aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"/>
                    </button>
                    <div className="collapse navbar-collapse" id="navbarTogglerDemo02">
                        <div className="navbar-nav mr-auto mt-2 mt-lg-0 form-inline my-2 my-lg-0"/>
                        <ul className="navbar-nav mt-2 mt-lg-0">
                            <li className="nav-item dropdown">
                                <a href="#" className="nav-link dropdown-toggle" data-toggle="dropdown" role="button"
                                   aria-expanded="false">
                                    Account
                                    <span className="caret"/></a>
                                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                                    <a className="dropdown-item" href="#">Action</a>
                                    <a className="dropdown-item" href="#">Another action</a>
                                    <a className="dropdown-item" href="#">Something else here</a>
                                </div>
                            </li>
                        </ul>
                    </div>
                </nav>
                <div className="menu center">
                    <nav className="navbar navbar-expand-lg navbar-light">
                        <ul className="navbar-nav">
                            {this.showMenu(menus)}
                        </ul>
                    </nav>
                </div>
            </div>
        );
    }

    showMenu = (menus) => {
        let result = null;
        if(menus.length > 0) {
            result = menus.map((menu, index) => {
                return (
                    <MenuLink
                        key={index}
                        name={menu.name}
                        to={menu.to}
                        exact={menu.exact}
                    />
                );
            })
        }
        return result;
    }
}

export default Menu;
